import React from "react";
/** @jsx jsx */
import { css, jsx } from "@emotion/core";

export default class Project extends React.Component {
  render() {
    return (
      <div
        className="card"
        css={css`
          cursor: pointer;
          transition: all 250ms;
          margin-bottom: 12px;

          & h4,
          & p {
            display: block;
          }

          &:hover {
            box-shadow: 0 8px #000;
            transform: translateY(-4px);
          }
        `}
      >
        <div
          css={css`
            width: 200px;
            margin: auto;
            padding-top: 66.66%;
            position: relative;
          `}
        >
          <img
            src={this.props.image}
            alt={this.props.name}
            css={css`
              display: block;
              object-fit: cover;
              border-radius: 4px;
              position: absolute;
              top: 0;
              left: 0;
              width: 100%;
              height: 100%;
            `}
          />
        </div>
        <h4>{this.props.name}</h4>
        <p>{this.props.flavor}</p>
      </div>
    );
  }
}
