/** @jsx jsx */
import { css, jsx } from "@emotion/core";

export default function Infobox(props) {
  return (
    <div>
      <h2>{props.section}</h2>
      <div
        className="card"
        css={css`
          ${props.styles};
          text-align: left;
          & h5,
          & h6,
          & p {
            margin: 0;
            font-size: 16px;
          }

          & p {
            margin-left: 20px;
          }

          & h6 span {
            color: #0080c6;
            font-weight: 700;
          }
        `}
        // dangerouslySetInnerHTML={{ __html: props.content }}
      >
        <h5>
          UCLA <span>(University of California - Los Angeles)</span>
        </h5>
      </div>
    </div>
  );
}
