/** @jsx jsx */
import { css, jsx } from "@emotion/core";

import house from "./house.svg";
import phone from "./phone.svg";
import mail from "./mail.svg";

export default function BioHeader(props) {
  return (
    <header
      className="card"
      css={css`
        display: flex;
        text-align: center;
        align-items: center;
        justify-content: flex-start;
      `}
    >
      <div
        css={css`
          margin-right: 10px;
        `}
      >
        <img
          src="/assets/profile.jpg"
          alt="Profile"
          css={css`
            display: block;
            height: 150px;
            border-radius: 4px;
            @media (max-width: 700px) {
              height: 100px;
            }
          `}
        />
      </div>
      <div
        css={css`
          flex-grow: 1;
        `}
      >
        <h1
          css={css`
            margin: 0;
            font-size: 48px;

            @media (max-width: 700px) {
              font-size: 16px;
            }
          `}
        >
          Thomas Adrian Gerard
        </h1>
        <h6
          css={css`
            margin: 0;
            font-size: 16px;
          `}
        >
          aspiring developer, designer, and digital-first journalist
        </h6>
        <div
          css={css`
            tet-align: left;
            & a {
              display: block;
              text-decoration: none;
              color: #000;
              @media (max-width: 700px) {
                font-size: 12px;
              }
            }
            & a img {
              margin-right: 4px;
              height: 16px;
              display: inline-block;
              vertical-align: middle;
            }
          `}
        >
          <a target="_blank" rel="noopener">
            <img src={house} />
            Bethesda, Maryland
          </a>
          <a href="tel:301-661-3996" target="_blank" rel="noopener">
            <img src={phone} />
            (301) 661-3996
          </a>
          <a href="mailto:thomasadrian@mac.com" target="_blank" rel="noopener">
            <img src={mail} />
            thomasadrian@mac.com
          </a>
        </div>
      </div>
    </header>
  );
}
