import React from "react";
/** @jsx jsx */
import { css, jsx } from "@emotion/core";

import arrow from "./arrow.svg";

export default class Project extends React.Component {
  constructor(props) {
    super(props);
    this.state = { exapanded: false, expandedCard: null };
    this.collapseProject = this.collapseProject.bind(this);
    this.card = React.createRef();
  }

  collapseProject() {}

  render() {
    return (
      <div
        className="card full-project"
        onClick={this.expandProject}
        ref={this.card}
        css={css`
          position: fixed;
          width: 100vw;
          height: 100vh;
          top: 0;
          left: 0;
          background-color: white;

          & h4,
          & p {
            text-align: left;
            margin: 4px auto;
          }

          &:hover {
            box-shadow: 0 8px #000;
            transform: translateY(-4px);
          }
        `}
        style={{ display: this.props.exapanded ? "block" : "none" }}
      >
        <div
          css={css`
            margin: auto;
            width: 100%;
            padding-top: 66.66%;
            position: relative;
          `}
        >
          <img
            src={this.props.image}
            alt={this.props.name}
            css={css`
              display: block;
              object-fit: cover;
              border-radius: 4px;
              position: absolute;
              top: 0;
              left: 0;
              width: 100%;
              height: 100%;
            `}
          />
        </div>
        <h4
          css={css`
            padding-top: 4px;
          `}
        >
          {this.props.name}
        </h4>
        <p>
          {this.props.flavor}
          <img
            src={arrow}
            alt="go"
            css={css`
              height: 12px;
              float: right;
              vertical-align: bottom;
              /* transform: rotate(-45deg); */
            `}
          ></img>
        </p>
      </div>
    );
  }
}
