import React from "react";
/** @jsx jsx */
import { Global, css, jsx } from "@emotion/core";

import arrow from "./arrow.svg";

export default class Project extends React.Component {
  constructor(props) {
    super(props);
    this.state = { expanded: false, expandedCard: null };
    this.collapseProject = this.collapseProject.bind(this);
    this.card = React.createRef();
  }

  collapseProject() {
    this.props.collapser();
  }

  render() {
    return (
      <div
        className="card full-project"
        ref={this.card}
        css={css`
          position: fixed;
          z-index: 10;
          margin: 0;
          background-color: #fff;
          top: 0;
          left: 0;
          right: 0;
          width: 100vw;
          height: 100%;
          border: none;
          border-radius: 0;
          text-align: center;
          overflow-y: scroll;
        `}
        style={{ display: this.props.expanded ? "block" : "none" }}
      >
        <div
          css={css`
            text-align: left;
            margin: auto;
            max-width: 600px;

            & h1,
            & p {
              text-align: left;
              margin: 4px auto;
            }

            & h5 {
              font-style: italic;
              font-weight: 400;
              margin: 0;
            }
          `}
        >
          {this.props.expanded && (
            <Global
              styles={css`
                html,
                body {
                  margin: 0;
                  height: 100%;
                  overflow: hidden;
                }
              `}
            />
          )}
          <div
            css={css`
              width: 100%;
              text-align: right;
            `}
          >
            <button
              onClick={this.props.collapser}
              css={css`
                background: none;
                border: none;
                margin: 0;
                font-weight: 800;
                font-size: 24px;
              `}
            >
              X
            </button>
          </div>
          <div
            css={css`
              margin: auto;
              width: 100%;
              padding-top: 66.66%;
              position: relative;
            `}
          >
            <img
              src={this.props.image}
              alt={this.props.name}
              css={css`
                display: block;
                object-fit: cover;
                border-radius: 4px;
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
              `}
            />
          </div>
          <h1
            css={css`
              padding-top: 4px;
            `}
          >
            {this.props.name}
          </h1>
          <h5>{this.props.flavor}</h5>
          <p></p>
          {this.props.link !== "" && (
            <div>
              <span
                css={css`
                  display: inline-block;
                  vertical-align: middle;
                `}
              >
                Visit the project here{" "}
              </span>
              <img
                src={arrow}
                alt="go"
                css={css`
                  display: inline-block;
                  height: 12px;
                  margin-left: 4px;
                  vertical-align: middle;
                  /* transform: rotate(-45deg); */
                `}
              ></img>
            </div>
          )}
        </div>
      </div>
    );
  }
}
