/** @jsx jsx */
import { css, jsx } from "@emotion/core";

import house from "./house.svg";
import phone from "./phone.svg";
import mail from "./mail.svg";

export default function BioHeader(props) {
  return (
    <header className="card">
      <div
        css={css`
          display: flex;
          text-align: center;
          align-items: center;
          justify-content: flex-start;
        `}
      >
        <div
          css={css`
            margin-right: 10px;
          `}
        >
          <img
            src="/assets/profile.jpg"
            alt="Profile"
            css={css`
              display: block;
              height: 150px;
              border-radius: 4px;
              @media (max-width: 700px) {
                height: 100px;
              }
            `}
          />
        </div>
        <div
          css={css`
            flex-grow: 1;
          `}
        >
          <h1
            css={css`
              margin: 0;
              font-size: 48px;

              @media (max-width: 700px) {
                font-size: 18px;
              }
            `}
          >
            Thomas Adrian Gerard
          </h1>
          <h6
            css={css`
              margin: 0;
              font-size: 16px;
              @media (max-width: 700px) {
                font-size: 11px;
              }
            `}
          >
            aspiring developer, designer, and digital-first journalist
          </h6>
          <div
            css={css`
              text-align: left;
              display: inline-block;
              margin: auto;
              @media (max-width: 700px) {
                display: none;
              }
              & a {
                display: block;
                text-decoration: none;
                color: #000;
                @media (max-width: 700px) {
                  font-size: 12px;
                }
              }
              & a img {
                margin-right: 4px;
                height: 16px;
                display: inline-block;
                vertical-align: middle;
              }
            `}
          >
            <a target="_blank" rel="noopener">
              <img src={house} />
              Bethesda, Maryland
            </a>
            <a href="tel:301-661-3996" target="_blank" rel="noopener">
              <img src={phone} />
              (301) 661-3996
            </a>
            <a
              href="mailto:thomasadrian@mac.com"
              target="_blank"
              rel="noopener"
            >
              <img src={mail} />
              thomasadrian@mac.com
            </a>
          </div>
        </div>
      </div>
      <div
        css={css`
          display: flex;
        `}
      >
        <div
          css={css`
            text-align: center;
            display: block;
            margin: auto;
            width: 100%;
            text-align: left;
            & a {
              display: block;
              text-decoration: none;
              color: #000;
              @media (max-width: 700px) {
                font-size: 12px;
                margin: 0 4px;
              }
            }
            & a img {
              margin-right: 4px;
              height: 16px;
              display: inline-block;
              vertical-align: middle;
            }
          `}
        >
          <a target="_blank" rel="noopener">
            <img src={house} />
            Bethesda, Maryland
          </a>
          <a href="tel:301-661-3996" target="_blank" rel="noopener">
            <img src={phone} />
            (301) 661-3996
          </a>
          <a href="mailto:thomasadrian@mac.com" target="_blank" rel="noopener">
            <img src={mail} />
            thomasadrian@mac.com
          </a>
        </div>
        <div>
          <div>
            <a>
              <svg
                width="12"
                height="12"
                viewBox="0 0 12 12"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <g clip-path="url(#clip0)">
                  <path
                    d="M12 2.27849C11.5585 2.47449 11.084 2.60649 10.586 2.66599C11.0945 2.36149 11.485 1.87899 11.6685 1.30399C11.193 1.58599 10.666 1.79099 10.105 1.90149C9.6565 1.42299 9.016 1.12399 8.308 1.12399C6.7185 1.12399 5.5505 2.60699 5.9095 4.14649C3.864 4.04399 2.05 3.06399 0.8355 1.57449C0.1905 2.68099 0.501 4.12849 1.597 4.86149C1.194 4.84849 0.814 4.73799 0.4825 4.55349C0.4555 5.69399 1.273 6.76099 2.457 6.99849C2.1105 7.09249 1.731 7.11449 1.345 7.04049C1.658 8.01849 2.567 8.72999 3.645 8.74999C2.61 9.56149 1.306 9.92399 0 9.76999C1.0895 10.4685 2.384 10.876 3.774 10.876C8.345 10.876 10.9275 7.01549 10.7715 3.55299C11.2525 3.20549 11.67 2.77199 12 2.27849Z"
                    fill="black"
                  />
                </g>
                <defs>
                  <clipPath id="clip0">
                    <rect width="12" height="12" fill="white" />
                  </clipPath>
                </defs>
              </svg>
            </a>
          </div>
          <div></div>
        </div>
      </div>
    </header>
  );
}
