import React from "react";
/** @jsx jsx */
import { css, jsx } from "@emotion/core";

import Button from "./Button";

export default function DownloadButton(props) {
  return (
    <a
      className="card"
      href={props.link}
      css={css`
        cursor: pointer;
        transition: all 250ms;
        flex-basis: 250px;
        min-width: 250px;
        flex-grow: 0;

        & h4,
        & p {
          text-align: left;
          margin: 4px auto;
        }

        &:hover {
          box-shadow: 0 8px #000;
          transform: translateY(-4px);
        }
      `}
    >
      {props.type}
    </a>
  );
}
