/** @jsx jsx */
import { css, jsx } from "@emotion/core";

import profile from "./profile.jpg";

import house from "./house.svg";
import phone from "./phone.svg";

export default function BioHeader(props) {
  return (
    <header
      className="card"
      css={css`
        display: flex;
        text-align: center;
        align-items: center;
        justify-content: flex-start;
      `}
    >
      <div
        css={css`
          margin-right: 10px;
        `}
      >
        <img
          src={"/assets/profile.jpg"}
          alt="Profile"
          css={css`
            display: block;
            height: 150px;
            border-radius: 4px;
          `}
        />
      </div>
      <div
        css={css`
          flex-grow: 1;
        `}
      >
        <h1
          css={css`
            margin: 0;
            font-size: 48px;
          `}
        >
          Thomas Adrian Gerard
        </h1>
        <h6
          css={css`
            margin: 0;
            font-size: 16px;
          `}
        >
          aspiring developer, designer, and digital-first journalist
        </h6>
        <div
          css={css`
            & a img {
              height: 16px;
              display: inline-block;
              vertical-align: middle;
            }
          `}
        >
          <a>
            <img src={house} />
            Bethesda, Maryland
          </a>
          <a>
            <img src={phone} />
            (301) 661-3996
          </a>
        </div>
      </div>
    </header>
  );
}
