import React from "react";
/** @jsx jsx */
import { css, jsx } from "@emotion/core";

import arrow from "./arrow.svg";

export default class Project extends React.Component {
  constructor(props) {
    super(props);
    this.state = { expanded: false, expandedCard: null };
    this.collapseProject = this.collapseProject.bind(this);
    this.card = React.createRef();
  }

  collapseProject() {
    this.setState({ expanded: false });
  }

  render() {
    return (
      <div
        className={`card full-project ${this.props.expanded ? "active" : ""}`}
        onClick={this.collapseProject}
        ref={this.card}
        css={css`
          position: fixed;
          z-index: 10;
          margin: 0;
          background-color: #fff;
          & h4,
          & p {
            text-align: left;
            margin: 4px auto;
          }
        `}
        style={{
          top: this.props.rect.top,
          left: this.props.rect.left,
          height: this.props.rect.height,
          width: this.props.rect.width,
        }}
      >
        <div
          css={css`
            margin: auto;
            width: 100%;
            padding-top: 66.66%;
            position: relative;
          `}
        >
          <img
            src={this.props.image}
            alt={this.props.name}
            css={css`
              display: block;
              object-fit: cover;
              border-radius: 4px;
              position: absolute;
              top: 0;
              left: 0;
              width: 100%;
              height: 100%;
            `}
          />
        </div>
        <h4
          css={css`
            padding-top: 4px;
          `}
        >
          {this.props.name}
        </h4>
        <p>
          {this.props.flavor}
          <img
            src={arrow}
            alt="go"
            css={css`
              height: 12px;
              float: right;
              vertical-align: bottom;
              /* transform: rotate(-45deg); */
            `}
          ></img>
        </p>
      </div>
    );
  }
}
