import React from "react";
/** @jsx jsx */
import { css, jsx } from "@emotion/core";

import arrow from "./arrow.svg";
import Full from "./Full";

export default class Project extends React.Component {
  constructor(props) {
    super(props);
    this.state = { exapanded: false, rect: { top: 0, width: 0 } };
    // this.expandProject = this.expandProject.bind(this);
    this.card = React.createRef();
  }

  expandProject() {
    this.setState({
      rect: this.card.current.getBoundingClientRect(),
      expanded: true,
    });
  }

  render() {
    return (
      <div
        className="card project-card"
        onClick={this.expandProject}
        ref={this.card}
        css={css`
          cursor: pointer;
          transition: all 250ms;
          flex-basis: 250px;
          min-width: 250px;
          flex-grow: 0;

          & h4,
          & p {
            text-align: left;
            margin: 4px auto;
          }

          &:hover {
            box-shadow: 0 8px #000;
            transform: translateY(-4px);
          }
        `}
      >
        <Full top={this.state.rect.top}></Full>
        <div
          css={css`
            margin: auto;
            width: 100%;
            padding-top: 66.66%;
            position: relative;
          `}
        >
          <img
            src={this.props.image}
            alt={this.props.name}
            css={css`
              display: block;
              object-fit: cover;
              border-radius: 4px;
              position: absolute;
              top: 0;
              left: 0;
              width: 100%;
              height: 100%;
            `}
          />
        </div>
        <h4
          css={css`
            padding-top: 4px;
          `}
        >
          {this.props.name}
        </h4>
        <p>
          {this.props.flavor}
          <img
            src={arrow}
            alt="go"
            css={css`
              height: 12px;
              float: right;
              vertical-align: bottom;
              /* transform: rotate(-45deg); */
            `}
          ></img>
        </p>
      </div>
    );
  }
}
