import React from "react";
/** @jsx jsx */
import { css, jsx } from "@emotion/core";

import arrow from "./arrow.svg";

export default class Project extends React.Component {
  showOverlay() {}

  render() {
    return (
      <div
        className="card"
        css={css`
          cursor: pointer;
          transition: all 250ms;

          & h4,
          & p {
            text-align: left;
          }

          &:hover {
            box-shadow: 0 8px #000;
            transform: translateY(-4px);
          }
        `}
      >
        <div
          css={css`
            width: 200px;
            margin: auto;
            padding-top: 66.66%;
            position: relative;
          `}
        >
          <img
            src={this.props.image}
            alt={this.props.name}
            css={css`
              display: block;
              object-fit: cover;
              border-radius: 4px;
              position: absolute;
              top: 0;
              left: 0;
              width: 100%;
              height: 100%;
            `}
          />
        </div>
        <h4>{this.props.name}</h4>
        <p>
          {this.props.flavor}{" "}
          <img
            src={arrow}
            alt="go"
            css={css`
              height: 12px;
              transform: rotate(-45deg);
            `}
          ></img>
        </p>
      </div>
    );
  }
}
