/** @jsx jsx */
import { css, jsx } from "@emotion/core";

export default function Infobox(props) {
  return (
    <div>
      <h2>{props.section}</h2>
      <div
        className="card"
        css={css`
          text-align: left;
          & h5,
          & h6,
          & p,
          & ul,
          & li {
            margin: 0;
            font-size: 16px;
          }

          & p {
            margin-left: 20px;
          }

          & ul {
            list-style: none;
          }

          & ul {
            list-style: none;
          }

          & h6 span {
            color: #0080c6;
            font-weight: 700;
          }

          ${props.styles};
        `}
        // dangerouslySetInnerHTML={{ __html: props.content }}
      >
        <h5>
          UCLA <span>(University of California - Los Angeles)</span>
        </h5>
        <h6>
          <span>Computer Science</span> and <span>Lingusitics</span>
        </h6>
        <h5>Fall 2017 - Spring 2020</h5>
        <p>
          <span>GPA:</span> 3.90
          <br /> <span>Relevant Coursework:</span>
          <ul>
            <li>Computational Lingusitics</li>
            <li>Automata Theory</li>
            <li>Programming Languages</li>
          </ul>
        </p>
      </div>
    </div>
  );
}
